select st.goods_id as goods_id, gd.name as name, gd.price as price,g.goods_type, g.publish_start as publish_start, g.publish_end as publish_end, sum(st.amount) as amount from stock as st inner join ( select goods_id,size,colors,max(create_datetime) as max_create_datetime from stock where del_flg = 0 group by goods_id,size,colors ) as wt on st.goods_id = wt.goods_id and st.size = wt.size and st.colors = wt.colors and st.create_datetime = wt.max_create_datetime inner join goods_detail as gd on st.goods_id = gd.goods_id and 0 = gd.del_flg
and gd.name like '%${name}%'
inner join goods as g on st.goods_id = g.id
and g.goods_type = #{gtype}
and date_format(g.publish_start,'%Y-%m-%d') >= date_format(#{publishStart},'%Y-%m-%d')
and date_format(g.publish_end,'%Y-%m-%d') <= date_format(#{publishEnd},'%Y-%m-%d')
group by goods_id, `name`, price,goods_type, publish_start, publish_end
having sum(st.amount) <= #{amount}
order by ${orderStatement} LIMIT #{length,jdbcType=INTEGER} OFFSET #{start,jdbcType=INTEGER};
