mapper.select:
select n.*, nc.title, nc.content,au.login_name, au.show_name, concat(p.first_name,p.second_name) as full_name, u.uuid
from notification as n
left join notification_content as nc on n.notifi_content_id = nc.id
left join admin_customer as au on n.user_id = au.id AND n.user_type=1
left join person as p on n.user_id = p.user_id AND n.user_type=2
left join user as u on n.user_id = u.id
where n.del_flg = 0

  and n.user_id = #{userId}


  and u.uuid = #{uuid}


  and n.user_type = #{userType}


  and n.notify_type = #{notifyType}


  and n.notified_device = #{notifiedDevice}


  and n.notify_status = #{notifyStatus}


  and n.push_status = #{pushStatus}


  and timediff(n.create_datetime,#{createDatetime}) >= 0

order by ${orderStatement}
LIMIT #{length,jdbcType=INTEGER} OFFSET #{start,jdbcType=INTEGER};

select count(n.id) as totalCount
from notification as n
where n.del_flg = 0

  and n.user_id = #{userId}


  and n.user_type = #{userType}


  and n.notify_type = #{notifyType}


  and n.notified_device = #{notifiedDevice}


  and n.notify_status = #{notifyStatus}


  and n.push_status = #{pushStatus}


  and timediff(n.create_datetime,#{createDatetime}) >= 0

